import React, { useState, useContext } from "react"
import data from "../Json/class.json"
import { Redirect, useParams } from "react-router-dom"
import { AuthContext } from "../Context/ContextProvider"

function Subjects(props) {
    const params = useParams()
    const classNote = data.find(item => item.id === params.classId)
    const subject = classNote["subjects"].find(item => item.id === params.subjectId)
    const context = useContext(AuthContext)
    const { login } = context
    console.log(login)
    const [parma, setParma] = useState(false)

    return (
        parma ?
            login ?
                <div style={{ marginTop: "5%" }}>
                    {
                        location.href = "https://ft15gm1a.revvsales.com/perma/DOC/5Oz5tO4jI5D94y5/English"
                    }
                </div>
                :
                <Redirect to="/account" />
            :
            <div style={{ width: "50%", marginLeft: "25%", display: "flex", flexDirection: "row", marginTop: "5%", textAlign: "center" }}>
                <div style={{ width: "50%" }}>
                    <img src="https://via.placeholder.com/400" alt="1" />
                </div>
                <div style={{ width: "50%", paddingTop: "10%" }}>
                    <h4>Class {params.classId}</h4>
                    <h2>{subject.name}</h2>
                    <button onClick={() => setParma(true)}>Parma Link</button>
                </div>
            </div>
    )
}

export default Subjects
